#include <stdlib.h>
#include <stdio.h>
#include <string.h>

static int	ft_nbrlen(unsigned int nbr){
	unsigned int	i;
	for (i = 0; nbr >= 10; nbr /= 10) i++;
	return (i + 1);
}

char	*ft_itoa(int n) {
	char	*str;
	int	len = ft_nbrlen(n), i = len - 1;
	str = (char *)malloc(sizeof(char) * len + 1);
	while (n >= 10) { str[i--] = n % 10 + 48; n /= 10; }
	str[i] = n % 10 + 48; str[len] = '\0';
	return (str);
}

int			good_char(char c) {
	if (c != '+' && c != '-' && c != '<' && c != '>'
           && c != '.' && c != ',' && c != '[' && c != ']')
		   return (0);
	return (1);
}

void  ft_sprintf(char *buf, char *str, int num) {
	int i = 0, j = 0, k = 0; char *tmp;
	while (buf[j]) j++;
	while (str[i]) {
		if (str[i] == '%') {
			tmp = ft_itoa(num);
			while (tmp[k]) buf[j++] = tmp[k++];
			free(tmp); i += 2;
		}
		buf[j++] = str[i++];
	}
	buf[j] = '\0';
}
const char* brainfuck_to_c (const char* src) {
	char *clean_str = malloc(sizeof(char)*1), *tmp_str;
	int i = 0, k = 0, len = 0, sign, brace = 0;
	clean_str[0] = '\0';
	while (src[k] && brace > -1) {
		while (!good_char(src[k]) && src[k]) k++;
		for (sign = 0; (src[k] == '+' || src[k] == '-' || !good_char(src[k])) && src[k]; k++) {
			if (src[k] == '+') sign++;
			else if (src[k] == '-') sign--;
		}
		if (sign != 0) {
			i = len;
			len += strlen("*p += ;\n") + ft_nbrlen(abs(sign)) + brace * 2;
			tmp_str = malloc(sizeof(char) * (len + 1)); tmp_str[0] = '\0';
			ft_sprintf(tmp_str, clean_str, 0);
			free(clean_str);
			for (int spaces = 0; spaces < brace; spaces++) {
				tmp_str[i++] = ' '; tmp_str[i++] = ' ';
			}
			tmp_str[i] = '\0';
			if (sign > 0)	ft_sprintf(tmp_str, "*p += %d;\n", abs(sign));
			else 			ft_sprintf(tmp_str, "*p -= %d;\n", abs(sign));
			clean_str = tmp_str;
		}
		for (sign = 0; (src[k] == '<' || src[k] == '>' || !good_char(src[k])) && src[k]; k++) {
			if (src[k] == '>') sign++;
			else if (src[k] == '<') sign--;
		}
		if (sign != 0) {
			i = len;
			len += strlen("p += ;\n") + ft_nbrlen(abs(sign)) + brace * 2;
			tmp_str = malloc(sizeof(char) * (len + 1)); tmp_str[0] = '\0';
			ft_sprintf(tmp_str, clean_str, 0);
			free(clean_str);
			for (int spaces = 0; spaces < brace; spaces++) {
				tmp_str[i++] = ' '; tmp_str[i++] = ' ';
			}
			tmp_str[i] = '\0';
			if (sign > 0)	ft_sprintf(tmp_str, "p += %d;\n", abs(sign));
			else 			ft_sprintf(tmp_str, "p -= %d;\n", abs(sign));
			clean_str = tmp_str;
		}
		else if (src[k] == '.') {
			i = len;
			len += strlen("putchar(*p);\n") + brace * 2;
			tmp_str = malloc(sizeof(char) * (len + 1)); tmp_str[0] = '\0';
			ft_sprintf(tmp_str, clean_str, 0);
			free(clean_str);
			for (int spaces = 0; spaces < brace; spaces++) {
				tmp_str[i++] = ' '; tmp_str[i++] = ' ';
			}
			tmp_str[i] = '\0';
			ft_sprintf(tmp_str, "putchar(*p);\n", 0);
			clean_str = tmp_str;
			k++;
		}
		else if (src[k] == ',') {
			i = len;
			len += strlen("*p = getchar();\n") + brace * 2;
			tmp_str = malloc(sizeof(char) * (len + 1)); tmp_str[0] = '\0';
			ft_sprintf(tmp_str, clean_str, 0);
			free(clean_str);
			for (int spaces = 0; spaces < brace; spaces++) {
				tmp_str[i++] = ' '; tmp_str[i++] = ' ';
			}
			tmp_str[i] = '\0';
			ft_sprintf(tmp_str, "*p = getchar();\n", 0);
			clean_str = tmp_str;
			k++;
		}
		else if (src[k] == '[') {
			while (!good_char(src[k]) && src[k]) k++;
      		if (src[k + 1] == ']') k += 2;
			else {
				i = len;
				len += strlen("if (*p) do {\n") + brace * 2;
				tmp_str = malloc(sizeof(char) * (len + 1)); tmp_str[0] = '\0';
				ft_sprintf(tmp_str, clean_str, 0);
				free(clean_str);
				for (int spaces = 0; spaces < brace; spaces++) {
					tmp_str[i++] = ' '; tmp_str[i++] = ' ';
				}
				tmp_str[i] = '\0';
				ft_sprintf(tmp_str, "if (*p) do {\n", 0);
				clean_str = tmp_str;
				brace++;
				k++;
			}
		}
		else if (src[k] == ']') {
			brace--;
			i = len;
			len += strlen("} while (*p);\n") + brace * 2;
			tmp_str = malloc(sizeof(char) * (len + 1)); tmp_str[0] = '\0';
			ft_sprintf(tmp_str, clean_str, 0);
			free(clean_str);
			for (int spaces = 0; spaces < brace; spaces++) {
				tmp_str[i++] = ' '; tmp_str[i++] = ' ';
			}
			tmp_str[i] = '\0';
			ft_sprintf(tmp_str, "} while (*p);\n", 0);
			clean_str = tmp_str;
			k++;
		}
	}
  	if (brace != 0) {
		free(clean_str);
		clean_str = malloc(sizeof(char) * 7);  clean_str[0] = '\0';
		ft_sprintf(clean_str, "Error!", 0);
	}
	printf("%s", clean_str);
	return (clean_str);
}

